ss'];								if($current_password == $old_password) {									$query = "UPDATE registration SET pass = '$new_password' WHERE id = '$id'";									mysqli_query($con, $query) or die('Error querying database.');								}								else {									$_SESSION['pass_update_error'] = 'Current Password does not match Password on File';								}									break; }							else {								$_SESSION['pass_update_error'] = 'If you want a new password, Please enter your current password and the new password twice for verification';								break; }														default:							/*empty default not being used */							echo 'Nothing Submited';							break;										}				}								mysqli_close($con);								?>								<form action="members_info.php" method="post" onsubmit="return verify(this)">					<?php 					if($_SESSION['login'] != 'admin') { ?>												<h2>Change your Phone Number</h2>						<?php echo $_SESSION['phone_update_error'] ?>						<p>Current Phone Number: <?php echo($phone); ?><a class="refresh_update" href="members_info.php"><img src="images/refresh.jpg" width="15" height="15" alt="refresh" title="refresh" />Click to see Updated Information</a></p>						<label>New Phone Number</label>						<input type="text" id="phone" name="phone" value="" class="input_txt" onkeyup="screenPhone(this);"/><br />						<input type="submit" value="Update Phone" name="submit" />												<h2>Change your Email Address</h2>						<?php echo $_SESSION['email_update_error'] ?>						<p>Current Email Address: <?php echo($email); ?><a class="refresh_update" href="members_info.php"><img src="images/refresh.jpg" width="15" height="15" alt="refresh" title="refresh" />Click to see Updated Information</a></p>												<table>							<tr>								<td><label>New Email Address:</label></td>								<td><input type="text" id="email" name="email" value="" class="input_txt" onChange="emailFormat(this)"/><br /></td>							</tr>								<tr>									<td><label>New Email Address